import { EXPENSE_SAVED, RESET_SAVED } from '../actions/types'


const initialState = {
    saved: false,
    updated:false
}

export default (state = initialState, { type, payload }) => {
    switch (type) {

        case EXPENSE_SAVED:
            return { ...state, saved: true }
            case RESET_SAVED:
                return {...state, saved:false, updated: false}
        default:
            return state
    }
}

export const resetSaved = () => ({ type: RESET_SAVED_FLAG });
