import {CLEAR_ERRORS, ADD_ERROR} from '../actions/types'

const initialState = {
    message: null 
}

export default (state = initialState, { type, payload }) => {
    switch (type) {

    case ADD_ERROR:
        return { ...state, ...payload }

        case CLEAR_ERROR:
            return { ...state, ...payload }
    
    default:
        return state
    }
}
