import React, { useState } from 'react';
import { connect } from 'react-redux';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    ButtonDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from 'reactstrap';

const NavBarComponent = (props) => {
    const [isOpen, setIsOpen] = useState(false);
    const [dropdownOpen, setDropDownOpen]= useState(false)

    const toggle = () => setIsOpen(!isOpen);
    const toggleButton =()=> setDropDownOpen(!dropdownOpen)

    function renderLoginorLogout() {
        const { isAuth } = props
        if (isAuth) {
            return (
                <ButtonDropdown isOpen={dropdownOpen} toggle={dropdownOpen}>
                    <DropdownToggle caret>
                        Button Dropdown
                    </DropdownToggle>
                    <DropdownMenu>
                        <DropdownItem header>Header</DropdownItem>
                        <DropdownItem disabled>Action</DropdownItem>
                        <DropdownItem>Another Action</DropdownItem>
                        <DropdownItem divider />
                        <DropdownItem>Another Action</DropdownItem>
                    </DropdownMenu>
                </ButtonDropdown>
            )
        } else {
            return (
                <NavItem>
                    <NavLink href="/login">Login</NavLink>
                </NavItem>
            )
        }
    }

    return (
        <div>
            <Navbar className='p-2' color="dark" dark expand="md">
                <NavbarBrand href="/">MERN expense</NavbarBrand>
                <NavbarToggler onClick={toggle} />
                <Collapse isOpen={isOpen} navbar>
                    <Nav className="ms-auto" navbar>
                        {renderLoginorLogout()}
                    </Nav>
                </Collapse>
            </Navbar>
        </div>
    );
}

const mapStateToProps = ({ auth }) => {
    return {
        isAuth: auth.isAuth,
    }
}
const NavBar = connect(mapStateToProps)(NavBarComponent)
export { NavBar };
