import React, { useState } from 'react'
import { connect } from 'react-redux';
import { Button, Modal, ModalHeader, ModalBody, Input, FormGroup, Label, FormFeedback } from 'reactstrap';
import { Formik } from 'formik'
import moment from 'moment'
import * as Yup from 'yup'

import { saveExpense } from '../actions'
import { FloatButton } from './FloatButton';

function useDidUpdate(callback, deps) {
    const hasMount = useRef(false)
    useEffect(() => {
        if (hasMount.current) {
            callback()
        } else {
            hasMount.current = true
        }
    }, deps)
}
function AddFormComponent(props) {
    useDidUpdate(() => {
        const { saved } = props
    })

function submit(values, bag) {
    props.saveExpense(values)
}
const [modal, setModal] = useState(false)
const toggle = () => setModal(!modal)
const now = moment().format('YYYY-MM-DD')
return (
    <div>
        <FloatButton onClick={toggle} />
        <Modal isOpen={modal} toggle={toggle}>
            <ModalHeader toggle={toggle}>Add expense</ModalHeader>
            <ModalBody>
                <Formik
                    initialValues={{
                        amount: '',
                        created: now
                    }}
                    onSubmit={submit}
                    validationSchema={Yup.object().shape({
                        amount: Yup.number().min(1).required(),
                        created: Yup.date().required(),
                    })}
                >

                    {({ errors,
                        touched,
                        handleBlur,
                        handleChange,
                        values,
                        isValid,
                        isSubmitting,
                        handleSubmit
                    }) => (
                        <div>
                            <FormGroup>
                                <Label>
                                    Amount
                                </Label>
                                <Input
                                    invalid={errors.amount && touched.amount}
                                    name='amount'
                                    value={values.amount}
                                    type='number'
                                    placeholder='Expense amount'
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                />
                                {errors.amount && touched.amount && (
                                    <FormFeedback>{errors.amount}</FormFeedback>
                                )}
                            </FormGroup>
                            <br />
                            <FormGroup>
                                <Label>
                                    Date
                                </Label>
                                <Input
                                    invalid={errors.created && touched.created}
                                    name='created'
                                    value={values.created}
                                    type='date'
                                    placeholder='expense amount'
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                />
                                {errors.created && touched.created && (
                                    <FormFeedback>{errors.created}</FormFeedback>
                                )}
                            </FormGroup>
                            <br />
                            <Button color="primary" onClick={handleSubmit} disabled={!isValid || isSubmitting} > Save </Button>
                        </div>
                    )}

                </Formik>
            </ModalBody>
        </Modal>
    </div>

)
}


const mapStateToProps = ({ expense }) => {
    return {
        saved: expense.saved
    }
}

const AddForm = connect(mapStateToProps, { saveExpense })(AddFormComponent)
export { AddForm }
